import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import navigation
import "./ContactForm.css"; 

const ContactForm = () => {
  const navigate = useNavigate(); // For navigation

  const initialFormData = {
    name: "",
    email: "",
    message: "",
    subject: "",
    consent: false,
  };

  const savedFormData = JSON.parse(localStorage.getItem("contactForm")) || initialFormData;
  const [formData, setFormData] = useState(savedFormData);
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    localStorage.setItem("contactForm", JSON.stringify(formData));
  }, [formData]);

  const validateForm = () => {
    let newErrors = {};

    if (!formData.name.trim()) newErrors.name = "‚ùå Name is required.";
    if (!formData.email.trim()) {
      newErrors.email = "‚ùå Email is required.";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "‚ùå Enter a valid email address.";
    }
    if (!formData.subject.trim()) newErrors.subject = "‚ùå Subject is required.";
    if (!formData.message.trim() || formData.message.length < 5) newErrors.message = "‚ùå Message must be at least 5 characters.";
    if (!formData.consent) newErrors.consent = "‚ùå You must agree to be contacted.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }

  const storeMessage = (newMessage) => {
    let messages = JSON.parse(localStorage.getItem("messages")) || [];
    messages.push(newMessage);
    localStorage.setItem("messages", JSON.stringify(messages));
  };

  // Modify handleSubmit to store email:
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      storeMessage({ 
        name: formData.name, 
        email: formData.email, 
        subject: formData.subject, 
        message: formData.message 
      });
      setSuccessMessage("‚úÖ Message sent successfully!");
      localStorage.removeItem("contactForm");
      setFormData(initialFormData);
      setTimeout(() => setSuccessMessage(""), 3000);
    }
  };


  return (
    <div className="contact-form-container">
      {successMessage && <p className="success-message">{successMessage}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input type="text" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} />
          {errors.name && <p className="error-message">{errors.name}</p>}
        </div>
        <div className="form-group">
          <label>Subject:</label>
          <input type="text" value={formData.subject} onChange={(e) => setFormData({ ...formData, subject: e.target.value })} />
          {errors.subject && <p className="error-message">{errors.subject}</p>}
        </div>
        <div className="form-group">
          <label>Message:</label>
          <textarea value={formData.message} onChange={(e) => setFormData({ ...formData, message: e.target.value })}></textarea>
          {errors.message && <p className="error-message">{errors.message}</p>}
        </div>
        <div className="form-group">
  <label>Email:</label>
  <input
    type="email"
    value={formData.email}
    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
  />
  {errors.email && <p className="error-message">{errors.email}</p>}
</div>

        <div className="form-group checkbox-group">
          <input type="checkbox" checked={formData.consent} onChange={(e) => setFormData({ ...formData, consent: e.target.checked })} />
          <label>I consent to be contacted.</label>
          {errors.consent && <p className="error-message">{errors.consent}</p>}
        </div>
        <button type="submit">Send Message</button>
      </form>
      <button onClick={() => navigate("/messages")} className="view-messages-btn">üì© View Messages</button>
    </div>
  );
};

export default ContactForm;
